    .selectAll("dot")
    .data(data)
    .join("circle")
        .attr("cx", function (d) { 
            console.log(d);
            return x(d.saturation); } )
        .attr("cy", function (d) { return y(d.lightness); } )
        .attr("r", function(d){
            if (d.artist == artist) {
                return 12;
